mapper.update:

	UPDATE	BOARD
	SET	BOARD_CONFIRM = 4
	WHERE	BOARD_NO=#{BOARD_NO}
	AND		BOARD_MODIFY_NO = #{BOARD_MODIFY_NO}

	
		BEGIN	
			UPDATE 
					BOARD
			SET
					BOARD_CONFIRM = '4'
			WHERE
					BOARD_NO = #{BOARD_NO};

			UPDATE	BOARD
			SET		BOARD_CONFIRM = '1'
			WHERE	BOARD_NO=#{BOARD_NO}
			AND		BOARD_MODIFY_NO = #{BOARD_MODIFY_NO};
		END;
